INSERT INTO `FUK_AKTIVNOST` VALUES (5391,3181,3237,'Отварање           Седнице НО',NULL,3,'Дневни ред','Отварање Седнице од стране Председника Надзорног одбора и констатује који су чланови Надзорног одбора присутни,а који су одсутни и разлоге њихове одсутности..','ПредседникНадзорног одбора','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:31');
INSERT INTO `FUK_AKTIVNOST` VALUES (5392,3181,3237,'Активирање      диктафона за      аудио снимање            седнице',NULL,4,'Аудио снимак','Отварање Седнице Надзорног одбора се прати  активирањем диктафона за аудио снимање.','Стручни сарадник','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:31');
INSERT INTO `FUK_AKTIVNOST` VALUES (5393,3181,3237,'Вођење Записника            седнице',NULL,5,'Записника са седнице','Седница Надзорног одбора се прати вођењем Записника од стране лица задужено за праћење седнице','Стручни сарадник','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:31');
INSERT INTO `FUK_AKTIVNOST` VALUES (5394,3181,3237,'Утврђивање              кворума',NULL,6,'','Председник Надзорног одбора утврђује кворум за одржавање седнице који је више од половине чланова Надзорног одбора.','ПредседникНадзорног одбора','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:31');
INSERT INTO `FUK_AKTIVNOST` VALUES (5395,3181,3237,'Излагање       Известиоца по        предложеним      тачкама Дневног              реда',NULL,7,'Предлог Одлуке','Председник Надзорног одбора даје реч Известиоцима по тачки дневног реда по реду пријављивања,стим да предност имају чланови Надзорног одбора.Председник ће дати реч мимо пријављивања известиоцу по тачки дневног реда, ако је затражена ради измене и допуне предлога или ако се од њега захтевају допунска објашњења у току расправе.','Известиоци','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:31');
INSERT INTO `FUK_AKTIVNOST` VALUES (5396,3181,3237,'Дискусија чланова    НО по тачкама    Дневног реда',NULL,8,'Предлог Одлуке','Председавајући руководи седницом,даје и одузима реч присутнима,стара се о реду и поштовању пословника,упозорава на прекорачење трајања излагања и удаљења од теме током излагања.У дискусији по тачкама Дневног реда чланови НО имају предност.','Чланови Надзорног одбора','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:31');
INSERT INTO `FUK_AKTIVNOST` VALUES (5397,3181,3237,'Усвајање                Одлуке',NULL,9,'Одлука','После завршене расправе по појединој тачки дневног реда,Надзорни одбор приступа доношењу одлука ,закључака или другог акта.Пре гласања председавајући седницом на основу писаног материјала, и усменог излагања известиоца утврђује предлог одлуке.Гласање на седници је јавно,сем ако Статутом није другачије одређено.Резултате гласања утврђује председавајући седницом.Одлука је донета ако је за предлог одлуке гласала већина чланова Надзорног одбора.','Чланови Надзорног одбора','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:31');
INSERT INTO `FUK_AKTIVNOST` VALUES (5398,3181,3237,'Утврђивање краја           седнице',NULL,10,'Записник','По завршеном расправљању и одлучивању о свим питањима која су на дневном реду седнице,председавајући седницом објављује да је седница закључена','ПредседникНадзорног одбора','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:31');
