INSERT INTO `FUK_AKTIVNOST` VALUES (5401,3182,3238,'Израда плана и програма контроле',NULL,2,'План и програм контроле','Руководилац или координатор послова Службе логистике и контроле израђује План и програм контроле на основу технолошког процеса aнализе рекламација, Захтева за контролу квалитета од стране генералног директора.','Руководилац службе логистике и контроле,   Координатор службе логистике и контроле,','Недељно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:32');
INSERT INTO `FUK_AKTIVNOST` VALUES (5402,3182,3238,'Подела задатака контролорима',NULL,3,'Радни налог за контролу','Руководилац или координатор послова Службе логистике и контроле на основу  Плана и програма контроле даје сарадницима за контролу процеса  упутство за контролисање и издаје   Радни налог за контролу у било ком од процеса пружања услуга.','Руководилац Службе логистике и контроле, Координатор Службе логистике и контроле, Сарадник за контролу процеса','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:32');
INSERT INTO `FUK_AKTIVNOST` VALUES (5403,3182,3238,'Контрола квалитета',NULL,4,'Евиденција о контролисању,  Извештај о неусаглашености, Радни налог за контролу, Радни налог процеса који се контролиш','Сарадник за контролу процеса контролише квалитет пружања услуга, врши евалуацију остварења процеса на местима предвиђеним за мерење процеса тзв. мерним тачкама у току и/или по завршеном процесу пружања услуга. По завршеној контроли, сарадник за контролу процеса потписује свој  Радни налог, а уколико није констатовао неусаглашеност и радни налог који је контролисао. Сарадник за контролу процеса води  Евиденцију о контролисању и прави  Извештај о неусаглашености уколико је исту констатовао.','Сарадник за контролу процеса,','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:32');
INSERT INTO `FUK_AKTIVNOST` VALUES (5404,3182,3238,'Евидентирање неусаглашености',NULL,5,'Регистар неусаглашености, Извештај о неусаглашености, Корективна / превентивна акција','Уколико је контролом квалитета установљена неусаглашеност, сарадник за контролу процеса евидентира исту и предлаже корективну/превентивну акцију у обрасцу  и предаје власнику процеса у којем је настала неусаглашеност на даљи третман у циљу разрешења неусаглашености и одговорном лицу Службе квалитета које води  Регистар неусаглашености у свим процесима рада ЈКП „Медиана“ Ниш.','Сарадник за контролу процеса, Власник контролисаног процеса, Одговорно лице за регистар неусаглашености на нивоу  предузећа.','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:32');
INSERT INTO `FUK_AKTIVNOST` VALUES (5405,3182,3238,'Провера спроведених мера',NULL,7,'Евиденција о контролисању, Извештај о неусаглашености','Сарадник за контролу процеса контролише да ли су спроведене мере отклониле предметну неусаглашеност, води Евиденцију о контролисању и уколико неусаглашеност није отклоњена понавља претходну активност (Евидентирање неусаглашености).','Сарадник за контролу процеса, Власник контролисаног процеса, Одговорно лице за регистар неусаглашености на нивоу предузећа.','У зависности од усвојених рокова за разрешење неусаглашености',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:32');
INSERT INTO `FUK_AKTIVNOST` VALUES (5406,3182,3238,'Контрола',NULL,8,'','Контрола реализације  установљененеусаглашеност квалитета','Координатор Службе логистике и контроле, Руководилац службе логистике и контроле','Дневно',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:32');
INSERT INTO `FUK_AKTIVNOST` VALUES (5407,3182,3238,'Верификација/ Овера усаглашености',NULL,9,'Радни налог за контролу','Уколико је контролом квалитета установљено да је услуга извршена у складу са технолошким процесом и/или су спроведене мере отклониле предметну неусаглашеност, сарадник за контролу процеса потписује  Радни налог за контролу чиме је оверио усаглашеност чињења по радном налогу са технолошким процесом.','Сарадник за контролу процеса','По завршетку провере',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:32');
