INSERT INTO `FUK_AKTIVNOST` VALUES (5410,3183,3239,'Мерење возила сарециклабилнимотпадом',NULL,2,'Потврда о пријему робе,Робна картица,Зап. о приспелим количинама, Дневна евид. пријемасек.сировина, Радно упутство за мерење,Свескаевид.сек.сировина,Записник о више испорученој роби.','По доласку возила са реона у РЈ „Рециклажни центар“, исто се упућује на колску вагу, где руковалац вагом врши мерење возила са довезеном количином отпада.','Руковалац вагом,Магационер,Пословођа за сортирање и прераду отпада, Возач специјалног возила,Комуални радник у рециклажи','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-06-04 10:47:25');
INSERT INTO `FUK_AKTIVNOST` VALUES (5411,3183,3239,'Истоваррециклабилноготпада',NULL,3,'Листић одваге,Путни налог за возило,Радни налог за сакупљање и прераду секундарних сировина,Тахо листић','Након мерења возило се упућује на истовар на прихватној траци линије за сепарацију. По истовару возило се мери празно, а за измерену тежину руковалац вагом издаје листић одваге  у два примерка, један за добављача а други остаје у РЈ „Рециклажни центар“. Листић одваге потписују руковалац вагом и представник добављача.','Пословођа,Возач специјалног возила,Комуални радник у рециклажи','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-06-04 10:47:25');
INSERT INTO `FUK_AKTIVNOST` VALUES (5412,3183,3239,'Складиштењеотпада',NULL,4,'Робна картица','Складиштење и вођење картица о улазу и излазу отпада.','Магационер','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-06-04 10:47:25');
INSERT INTO `FUK_AKTIVNOST` VALUES (5413,3183,3239,'Задужење ираздужењеотпада',NULL,5,'- Потврда о пријему робе,Листић одваге,Робне картице,Записник о раздужењу,Записник о више испорученој роби','Преузети отпад прати Отпремница правног лица и Документ о кретању отпада, на основу чега магационер издаје Потврду о пријему робе . Магационер попуњава и Робну картицу  за сваку врсту отпада. Такође, попуњава Записник о раздужењу, у случају кала, растура, лома и Записник о више испорученој роби  када дође до таквих случајева при пријему отпада. Записник о раздужењу , и по потреби, Записник о више испорученој роби  се израђује у 2 примерка и потписује их магационер и руководилац Радне јединице. Један примера остаје у радној јединици , док се други примерак шаље Економском сектору.','-МагационерВозач специјалног возила,Комуални радник у рециклажи','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-06-04 10:47:25');
