INSERT INTO `FUK_AKTIVNOST` VALUES (5416,3184,3240,'Пријем захтева                   грађана',NULL,2,'Пријем захтева  ЈП Дирекција за изградњу града','Сви захтеви грађана упућују се    Дирекцији  за изградњу града.грађани и управници зграда, кућни савети као и пријаве преко апликације Еколовац.','Руководилац РЈ Уређења и одржавања мобилијара','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-06-04 19:40:00');
INSERT INTO `FUK_AKTIVNOST` VALUES (5417,3184,3240,'Преиспитивање  и обрада захтева',NULL,3,'','Преиспитивање и обрада  захтева, и усаглашавање приоритета са надзорним органом из Дирекције за изградњу града;','Руководилац РЈ Уређења и одржавања мобилијара са екипом','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-06-04 19:40:00');
INSERT INTO `FUK_AKTIVNOST` VALUES (5418,3184,3240,'Формирање                     нових                мобилијара',NULL,4,'Грађевински дневник','Приликом формирања нових парковских површина у Граду, по налогу ивеститора (ЈП „Дирекција за изградњу Града“), добијамо Пројекат укоме су тачно дефинисане позиције, врста и изглед нових мобилијара, које треба поставити на тим површинама;','Руководилац РЈ Уређења и одржавања мобилијара са екипом','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-06-04 19:40:00');
INSERT INTO `FUK_AKTIVNOST` VALUES (5419,3184,3240,'Контрола',NULL,5,'','Контролом се утврђује да ли су правилно дефинисане врсте радова и услуга и време и начин извршења истих.','Руководилац РЈ Уређења и одржавања мобилијара и пословођа','Након израдемобилијара',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-06-04 19:40:00');
INSERT INTO `FUK_AKTIVNOST` VALUES (5420,3184,3240,'Контрола',NULL,6,'','Одобравање од стране надзора. Упућивање документације о изградњи новог мобилијара надзору дирекције за изградњу града','Надзор ЈП„Дирекције за изградњу града“','Након завршеног мотирања',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-06-04 19:40:00');
