INSERT INTO `FUK_PROCEDURA` VALUES (3069,3001,3035,3012,3062,'БЗР2.3','Процедура  прегледа и испитивања громобранске инсталације објеката у предузећу','1.',NULL,'Руководилац Службе безбедности и здравља на раду','Сарадник за заштиту од пожара','Процес заштите од пожара','Заштита објеката и запослених од  избијања пожара услед удара грома.','Процедура се примењује у ЈКП „Медиана“ Ниш.','','Сарадник за ЗОП је одговоран за спровођење процедуре.\nРуководилац Службе БЗР је одговоран за надзор и контролу процедуре.','Закон о заштити од пожара | Правилник о техничким нормативима за заштиту од пожара од атмосферског пражњења.','БЗР - Безбедност и здравље на раду;\nЗОП - Заштита од пожара',NULL,NULL,NULL,NULL,1,'2023-03-08 22:53:17');
