INSERT INTO `FUK_RIZIK` VALUES (4109,3127,NULL,3001,NULL,'П5','Велико зачепљење у димњаку или димоводном каналу услед нередовног одржавања или лошег квалитете енергента.',NULL,NULL,'Руководилац РЈ“ Димничар“',2,2,4,'Руководилац РЈ“ Димничар“','Указивање корисницима на важност редовног одржавања димоводних и ложишних објеката и уређаја, на правилан избор енергента и проверу ефикасног сагоревања у ложишту. Измена законских и подзаконских докумената.','Руководилац РЈ“ Димничар“','2019-12-16',NULL,NULL,NULL,NULL,NULL,NULL,1,'2023-07-02 15:57:19');
