INSERT INTO `FUK_RIZIK` VALUES (4089,3062,NULL,3001,NULL,'БЗР2','Ризик од експлозије услед цурења судова под притиском.',NULL,NULL,'Руководилац службе за БЗР',2,1,2,'Стручни сарадник за БЗР','Контрола од стране акредитованог правног лица. Спољашњи прегледи опреме под притиском, на две године. Ванредни спољашњи преглед опреме, уколико запослени пријави служби БЗР проблем у функционисању система или уколико Служба БЗР, контролама, детектује проблем.','Стручни сарадник за БЗР','2019-12-16',NULL,NULL,NULL,NULL,NULL,NULL,1,'2023-07-02 15:57:19');
INSERT INTO `FUK_RIZIK` VALUES (4088,3062,NULL,3001,NULL,'БЗР2','Ризик од експлозија судова под притиском, уколико вентили сигурности нису у исправном стању',NULL,NULL,'Руководилац службе за БЗР',2,1,2,'Стручни сарадник за БЗР','Поред редовне контроле,која се обавља на годину дана, служба БЗР би требало, приликом месечних контрола објеката на свим локацијама, да се информише од запослених да ли постоји проблем у функционисању судова под притиском. У случају да постоји, требало би позвати изабраног извршиоца услуге да изврши ванредни преглед.','Стручни сарадник за БЗР','2019-12-16',NULL,NULL,NULL,NULL,NULL,NULL,1,'2023-07-02 15:57:19');
