INSERT INTO `Rizikfinal` VALUES ('ПО4','Процес праћења и примене прописа','Сврха процеса је утврдити след активности којима се врши усклађивање законске регулативе како би иста могла правилно да се примени у раду предузећа. Циљ процеса је правовремено информисање о законским захтевима.','Нередовно праћење закона','Руководилац службе правних послова',3,2,6,'Стална контрола измењених законских прописа .и обавештавање руководилаца организационих јединица о истом.','Руководилац службе правних послова','16.12.2019.');
