INSERT INTO `Rizikfinal` VALUES ('П1','Процес усаглашења обавеза и потраживања','Усаглашавање обавеза и потраживања по свим основама','Погрешно књижење (погрешан конто, погрешан износ)','Руководилац службе наплате прихода',3,1,3,'Провера комплетне документације за књижење. Софтверска провера података','Руководилац службе наплате прихода','16.12.2019.');
